let command_line_whitelist = process_create_whitelist | project replace("'", "", replace('"', '', process_command_line));
let hash_whitelist = process_create_whitelist | project hash_sha256;
Sysmon
| where isnotempty(process_guid)
| where process_guid == "{process_guid_selection}"
| where EventID == 1
| where process_parent_path !in~ (process_ppath_whitelist) and process_path !in~ (process_path_whitelist) and replace('"', '', tostring(process_command_line)) !in~ (command_line_whitelist) and hash_sha256 !in~ (hash_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, UserName, process_parent_path, process_path, file_name, process_parent_command_line, process_command_line, process_parent_guid, hash_sha256, process_id, process_parent_id

